// src/components/BookList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BookItem from './BookItem';
import BookForm from './BookForm';

function BookList() {
  const [books, setBooks] = useState([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [search, setSearch] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);

  // Функция для получения списка книг с сервера
  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/books', {
        params: {
          page,
          limit: 10,
          search,
        },
      });
      setBooks(response.data.books);
      setTotal(response.data.total);
    } catch (error) {
      console.error('Ошибка при получении книг:', error);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, [page, search]);

  // Обновление списка после добавления/редактирования/удаления
  const refreshBooks = () => {
    fetchBooks();
  };

  return (
    <div>
      <h1>Склад книг</h1>
      <div>
        <input
          type="text"
          placeholder="Поиск по названию, автору или жанру"
          value={search}
          onChange={(e) => {
            setSearch(e.target.value);
            setPage(1);
          }}
        />
      </div>
      <BookForm
        refreshBooks={refreshBooks}
        selectedBook={selectedBook}
        setSelectedBook={setSelectedBook}
      />
      <ul>
        {books.map((book) => (
          <BookItem
            key={book._id}
            book={book}
            refreshBooks={refreshBooks}
            setSelectedBook={setSelectedBook}
          />
        ))}
      </ul>
      <div>
        <button onClick={() => setPage((prev) => Math.max(prev - 1, 1))} disabled={page === 1}>
          Предыдущая
        </button>
        <span>
          Страница {page} из {Math.ceil(total / 10)}
        </span>
        <button onClick={() => setPage((prev) => prev + 1)} disabled={page >= Math.ceil(total / 10)}>
          Следующая
        </button>
      </div>
    </div>
  );
}

export default BookList;
