// src/components/BookItem.js
import React from 'react';
import axios from 'axios';

function BookItem({ book, refreshBooks, setSelectedBook }) {
  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:5000/api/books/${book._id}`);
      refreshBooks();
    } catch (error) {
      console.error('Ошибка при удалении книги:', error);
    }
  };

  return (
    <li>
      <div>
        <strong>{book.title}</strong> — {book.author} ({book.genre}) — {book.price} руб.
      </div>
      <div>
        <button onClick={() => setSelectedBook(book)}>Редактировать</button>
        <button onClick={handleDelete}>Удалить</button>
      </div>
    </li>
  );
}

export default BookItem;
